import React, { Component } from 'react'
import Stepone from './steps/Stepone';
import Steptwo from './steps/Steptwo';
import Steptre from './steps/Steptre';
import axios from 'axios'

export default class Verification extends Component {

    state={
        step:1,
        phone:'',
        country:'',
        street:'',
        area:'',
        address:'',
        postal_code:'',
        poa:'',
        poi:'',
        response:'',
        show:false
    }

    nextstep = () =>{

        const { step } =this.state
        this.setState({step:step+1})
    }

    prevstep = () =>{

        const { step } =this.state
        this.setState({step:step-1})
    }


    handlechange=(event)=>{

        this.setState({[event.target.name]:event.target.value})

    }
    onsubmit=async(event)=>{
        event.preventDefault()

        console.log(this.state.poa+' '+this.state.poi)
        await axios.post('https://api.sortika.com/trader/documents/verify',{
            phone:this.state.phone,
            country:this.state.country,
            area:this.state.area,
            street:this.state.street,
            address:this.state.address,
            postal_code:this.state.postal_code,
            poi:this.state.poi,
            poa:this.state.poa

        }).then((res)=>{
            console.log(res)
            this.setState({response:res.data.message,show:true})
        }).catch((err)=>{
            console.log(err.response.data)
                if(err.response.data){
                    this.setState({response:err.response.data.message,show:true})
                }
        })
        // this.setState({show:true})
    }
  render() {
    const {step,phone,country,street,adress,postal_code,poa,poi,area} =this.state
    const values={phone,country,street,adress,postal_code,area,poa,poi}

    switch(step){
        case 1:
            return (
                <Stepone 
                nextstep={this.nextstep}
                handlechange={this.handlechange}
                values={values}
            />
            )

        case 2:
            return (
                <Steptwo
                     nextstep={this.nextstep}
                     prevstep={this.prevstep}
                        handlechange={this.handlechange}
                        values={values}
                />
            )
        case 3:
                return (
                    <Steptre
                          prevstep={this.prevstep}
                            handlechange={this.handlechange}
                            values={values}
                            submit={this.onsubmit}
                            modalshow={this.state.show}
                            modalhide={()=>this.setState({show:false})}
                    />
                )

         default:
                 return null


    }
  }
}
